    ushort* GenerateIdentityTable()
    {
    	int size = 1 << 16;
    	auto res = new ushort[size];

    	for (int i = 0; i < size; ++i)
    		res[i] = i;

    	return res;
    }

    ushort* GenerateLowerMatchTable()
    {
    	ushort* res = GenerateIdentityTable();
    	int nletters = sizeof(lowers)/sizeof(ushort);

    	for (int i = 0; i < nletters; ++i)
    		if (res[uppers[i]] == uppers[i]) // fix case when two different small letters correspond one big (as for I)
    			res[uppers[i]] = lowers[i];

    	return res;
    }

    ushort* GetLowerMatchTable()
    {
    	static ushort* lowerMatchTable = GenerateLowerMatchTable();
    	return lowerMatchTable;
    }

    ushort* GetIdentityTable()
    {
    	static ushort* lowerMatchTable = GenerateIdentityTable();
    	return lowerMatchTable;
    }

